// HogeExists checks if the Hoge row exists.
func HogeExists(ctx context.Context, exec boil.ContextExecutor, iD int) (bool, error) {
	var exists bool
	sql := "select exists(select 1 from `hoge` where `id`=? limit 1)"

	if boil.IsDebug(ctx) {
		writer := boil.DebugWriterFrom(ctx)
		fmt.Fprintln(writer, sql)
		fmt.Fprintln(writer, iD)
	}
	row := exec.QueryRowContext(ctx, sql, iD)

	err := row.Scan(&exists)
	if err != nil {
		return false, errors.Wrap(err, "entity: unable to check if hoge exists")
	}

	return exists, nil
}

// Exists checks if the Hoge row exists.
func (o *Hoge) Exists(ctx context.Context, exec boil.ContextExecutor) (bool, error) {
	return HogeExists(ctx, exec, o.ID)
}
